#ciclo do while mientras el likelihood no converga al valor buscado (En python no existe el do while, voy a tener que usar un while true con break)

iter=0
while True:

  #calculo paso E y M
  iter= iter + 1
  log_gamma,log_xi= paso_e(conjunto_entrenamiento,hmm_est)
  mu, sigma, A = paso_m(conjunto_entrenamiento,log_gamma,log_xi)

  #actualizo los parametros del modelo
  hmm_est.means = mu
  hmm_est.vrs = sigma
  hmm_est.trans = A

  #Calculo likelihood
  likeli_hood.append(logfw(conjunto_entrenamiento,hmm_est)[0])


  #si son mas de 20 iteraciones deja de graficar y solo calcula (para que no se rompa, ni se haga lento)
  if iter <= 20:

    plt.subplot(5,2,iter)

    for x in range(3):
      u1 = (x+1)*np.cos(np.arange(0,2*pi,0.01))
      u2 = (x+1)*np.sin(np.arange(0,2*pi,0.01))

      AX1_menos1 = np.linalg.cholesky(hmm_est.vrs[0])
      V_x1 = np.transpose(AX1_menos1.dot(np.matrix([u1,u2])))

      AX2_menos1 = np.linalg.cholesky(hmm_est.vrs[1])
      V_x2 = np.transpose(AX2_menos1.dot(np.matrix([u1,u2])))

      AX3_menos1 = np.linalg.cholesky(hmm_est.vrs[2])
      V_x3 = np.transpose(AX3_menos1.dot(np.matrix([u1,u2])))


      plt.plot(V_x1[:,0]+ hmm_est.means[0,0],V_x1[:,1]+ hmm_est.means[0,1],'r')
      plt.plot(V_x2[:,0]+ hmm_est.means[1,0],V_x2[:,1]+ hmm_est.means[1,1],'g')
      plt.plot(V_x3[:,0]+ hmm_est.means[2,0],V_x3[:,1]+ hmm_est.means[2,1],'b')


    plt.plot( hmm_est.means[0][0],  hmm_est.means[0][1] , 'rx' )
    plt.plot( hmm_est.means[1][0],  hmm_est.means[1][1], 'gx')
    plt.plot( hmm_est.means[2][0],  hmm_est.means[2][1], 'bx')

    plt.xlabel("Frecuencia 1 [Hz]")
    plt.ylabel("Frecuencia 2 [Hz]")

    plt.title("Iteración %i" %iter)
    plt.scatter(conjunto_entrenamiento[:,0],conjunto_entrenamiento[:,1],alpha = 0.6, color=[np.exp(log_gamma[k,:]) for k in range(len(log_gamma))])
    plt.legend(['Estado 1', 'Estado 2', 'Estado 3'])
    plt.grid()


    #CONDICION DE CORTE
    if likeli_hood[iter] - likeli_hood[iter-1] < cota_error_likehood:
      break

#Grafico el likelihood
fig = plt.figure(figsize=(12,8))
fig.tight_layout()
plt.subplot(1,1,1)


iteraciones=np.arange(1,len(likeli_hood),1) #arranco desde la uno para verlo mejor por la escala
plt.scatter(iteraciones,likeli_hood[1:])
plt.xlabel("Iteración")
plt.ylabel("Amplitud")
plt.grid()
plt.title("Evolución del Log-likelihood \n",fontsize=18)
plt.show()

